package week1day1;

public class Learningdatatypes {
	
	static boolean z;
	static byte y; 
	static int x;
	static float v;
	static double u;
	static char t;
	static short w;
	static long lo;
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
		boolean b = true;
		byte f = 6; 
		int a =4;
		float c =  8.6f;
		double d = 8.90233;
		char e= 'i';
		short s = 123;
		long l= 31231;
		
		
		System.out.println("The value of int variable from main method------>"+a);
		System.out.println("The value of float variable from main method------>"+c);
		System.out.println("The value of double variable from main method------>"+d);
		System.out.println("The value of char variable from main method------>"+e);
		System.out.println("The value of short variable from main method------>"+s);
		System.out.println("The value of long variable from main method------>"+l);
		System.out.println("The value of byte variable from main method------>"+f);
		System.out.println("The value of boolean variable from main method------>"+b);
		
		
		System.out.println("The value of int variable in class level------>"+x);
		System.out.println("The value of float variable in class level------>"+v);
		System.out.println("The value of double variable in class level------>"+u);
		System.out.println("The value of char variable in class level------>"+t);
		System.out.println("The value of short variable in class level------>"+w);
		System.out.println("The value of long variable in class level------>"+lo);
		System.out.println("The value of byte variable in class level------>"+y);
		System.out.println("The value of boolean variable in class level------>"+z);
	}

}
