package week1day2;

public class LearningSingleArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char [] student1 = {'S','A','B','E','F'};
		
		char [] student2 = {'B','E','B','A','S'};

		
		System.out.println("The student1 grade are-------->"+student1[0]);
		System.out.println(student1[1]);
		System.out.println(student1[2]);
		System.out.println(student1[3]);
		System.out.println(student1[4]);
		
		System.out.println("The student2 grade are-------->"+student2[0]);
		System.out.println(student2[1]);
		System.out.println(student2[2]);
		System.out.println(student2[3]);
		System.out.println(student2[4]);
	}

}
