package week1day2;

public class LearningSingleArrayWithLooping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char [] student1 = {'S','A','B','E','F'};

		char [] student2 = {'B','E','B','A','S'};


		for(int j=0; j<=4; j++) {

			System.out.println("The student1 grade are-------->"+student1[j]+"\n");


		}

		for(int k=0; k<=4; k++) {



			System.out.println("The student2 grade are-------->"+student2[k]+"\n");
		}


	}

}
